<div class="blocks__collection">
	<h3 class="main__title"><?php esc_html_e( 'Control Setting for Blocks', 'rootblox' ); ?></h3>
	<p><?php esc_html_e( 'Easily enable or disable individual blocks based on your needs. Disabling unused blocks not only declutters your editor but can also improve performance by reducing unnecessary processing.', 'rootblox' ); ?></p>

	<ul class="blocks__list">
		<?php
		if ( ! empty( $blocks_collection ) ) {
			foreach ( $blocks_collection as $block_name => $block ) {
				?>
				<li class="block content__wrap">
					<div>
						<span class="block__icon"><?php echo $block['icon']; ?></span>
						<span class="block__label"><?php echo esc_html( $block['label'] ); ?>
	
							<?php
							if ( filter_var( $block['premium'], FILTER_VALIDATE_BOOLEAN ) ) {
								?>
								<span class="pro__label"><?php esc_html_e( 'Pro', 'rootblox' ); ?></span>
								<?php
							}
							?>
						</span>
					</div>
					<?php
					$classes   = array();
					$classes[] = 'block__toggle-switch';
					$classes[] = ! rootblox_is_premium() && filter_var( $block['premium'], FILTER_VALIDATE_BOOLEAN ) ? 'disabled' : '';
					$classes[] = filter_var( $block['premium'], FILTER_VALIDATE_BOOLEAN ) ? 'pro' : '';

					$block_status = get_option( 'rootblox_block_status' );
					$checked      = true;
					if ( isset( $block_status[ $block_name ] ) && ! empty( $block_status[ $block_name ] ) && 'inactive' === $block_status[ $block_name ] ) {
						$checked = false;
					}

					?>
					<label class="<?php echo esc_html( implode( ' ', array_map( 'sanitize_html_class', array_values( $classes ) ) ) ); ?>">
						<input class="block__status" type="checkbox" data-block-name="<?php echo esc_html( $block_name ); ?>" <?php echo $checked ? 'checked' : ''; ?>>
						<span class="slider"></span>
					</label>
				</li>
				<?php
			}
		}
		?>
	</ul>
</div>